        int s = nums[0];
        while(start <= end && end < n) {
            if(nums[end] > nums[end - 1]) {
                s += nums[end];
                ans = Math.max(ans, s);
                end ++;
            } else {
                start = end;
                end = start + 1;
                ans = Math.max(ans, s);
                s = nums[start];
            }

        }
        return ans;
    }
}
