class Solution {
public:
    int orangesRotting(vector<vector<int>>& grid) {
        // Question involves BFS
        // there can be multiple rotten oranges, so its a multi source BFS
        // since we need to get the time also when the orange is rotting
        // and for any orange that will be time(parent)+1
        // so we can take queue of  ( vector of size 3 ) 
        // v[0]:row , v[1]:column , v[2]:time
        int time = 0; // to get the max time
        queue<vector<int>> q;
        int n=grid.size();
        int m=grid[0].size();

        // insert all the rotten oranges as source in queue
        for( int i=0; i<n; i++ ){
            for( int j=0; j<m; j++ ){
                if( grid[i][j]==2 )
                    q.push( {i,j,0} );
            }
        }     

        int x[]={0,0,1,-1};
        int y[]={1,-1,0,0};

        while( !q.empty() ){

            auto f = q.front(); // rotten orange at the front of queue

            // now check all its adjacent cells
            for( int i=0; i<4; i++){
                int r=f[0]+x[i] ,c=f[1]+y[i];
                if( r<0 || r>=n || c<0 || c>=m || grid[r][c]!=1 ) continue; // check if it is valid fresh orange cell
                grid[r][c]=2; // make the fresh orange as rotten
                int t = f[2] + 1; // time at which this orange became rotten
                time = max( time, t ); // update the max time to return ans answer
                q.push( {r,c,t} ); // push this rotten orange to queue
            }

            q.pop();
        }

        // now the max time is stored in "time"
        // but before returning this we need to check if there is any fresh orange left
        for( int i=0; i<n; i++ ){
            for( int j=0; j<m; j++ ){
                if( grid[i][j]==1 )
                    return -1;
            }
        } 

        return time;
    }
};
