class Solution {

    class indexies{
        int x;
        int y;

        indexies(int x,int y){
            this.x=x;
            this.y=y;
        }

    }

    public int orangesRotting(int[][] grid) {

        Queue<indexies> q=new LinkedList<>();
        int ones=0;
        int m=grid.length,n=grid[0].length;

        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
				//rotton oranges positions
                if(grid[i][j]==2){
                    q.add(new indexies(i,j));
                }
				//count the fresh oranges
                else if(grid[i][j]==1){
                    ones++;
                }
            }
        }

		//if no rotton but we have fresh oranges so 0 time taken
        if(q.size()==0 && ones>0) 
            return -1;
		//we don't have any fresh oranges
        if(ones==0)
            return 0;

        int ans=0;

        int dir[][]={{0,1},{0,-1},{-1,0},{1,0}};

        while(!q.isEmpty()){

			//add and travel
            int size=q.size();
            boolean f=false;              

			//moves to add rotton vertices
            for(int ii=0;ii<size;ii++){
                indexies temp=q.poll();

                //move to all the adjacent vertices
                for(int i=0;i<4;i++){
                    int new_x=temp.x+dir[i][0];
                    int new_y=temp.y+dir[i][1];

                    //if we find fresh anywhere in the grid
                    if(new_x>=0 && new_y>=0 && new_x<m && new_y<n && grid[new_x][new_y]==1){

						//make it rotton and add position
                        grid[new_x][new_y]=2;
                        q.add(new indexies(new_x,new_y));
                        ones--;

                        //we take the time to make all the adjacent fresh to rotton
                        //means we found the fresh oranges and make it rotton
                        f=true;

                    }

                }

            }

            //if we found fresh and make it rotton means we consume time so increase it
            if(f){
                ans++;
            }

        }

        //somwhere in the grid fresh is present means no possible way
        if(ones>0)
            return -1;

        return ans;
    }
}
