class Solution:
    def orangesRotting(self, grid: List[List[int]]) -> int: 
        maxx = 0
        for i in range(len(grid)):
            for j in range(len(grid[i])):
                if grid[i][j] == 2:
                    self.dfs(grid, i, j, 0, True)

        for i in range(len(grid)):
            for j in range(len(grid[i])):
                if grid[i][j] == 1:
                    return -1
                maxx = max(abs(grid[i][j]), maxx)

        return maxx

    def dfs(self, grid, i, j, counter, start):
        if i < 0 or j < 0 or i >= len(grid) or j >= len(grid[i]) or grid[i][j] == 0 or (grid[i][j] < 0 and -grid[i][j] < counter):
            return 
        if start is False and grid[i][j] == 2:
            return 

        grid[i][j] = -counter

        self.dfs(grid, i+1, j, counter + 1, False)
        self.dfs(grid, i-1, j, counter + 1, False)
        self.dfs(grid, i, j+1, counter + 1, False)
        self.dfs(grid, i, j-1, counter + 1, False)

