class Solution:
    def orangesRotting(self, grid: List[List[int]]) -> int:
        # store in dictionary all coordinations of oranges, sorted by rotten/fresh/empty
        my_dict = defaultdict(list)
        for i in range(len(grid)):
            for j in range(len(grid[i])):
                my_dict[grid[i][j]].append([i, j])

        count = 0
        while len(my_dict[1]) > 0:
            count += 1
            prev_len = len(my_dict[1])
            add_list = []
            # if there are no rotten oranges
            if len(my_dict[2]) == 0: 
                # and there are no fresh ones
                if len(my_dict[1]) == 0:
                    return 0
                # there are fresh ones but no rotten
                else:
                    return -1

            # check all four sides of each rotten orange
            for x, y in my_dict[2]:
				# top:
                if x > 0 and [x - 1, y] in my_dict[1]:
                    my_dict[1].remove([x - 1, y])
                    add_list.append([x - 1, y])

				# right:
                if y < len(grid[0]) and [x, y + 1] in my_dict[1]:
                    my_dict[1].remove([x, y + 1])
                    add_list.append([x, y + 1])

				# down:
                if x < len(grid) and [x + 1, y] in my_dict[1]:
                    my_dict[1].remove([x + 1, y])
                    add_list.append([x + 1, y])

				# left:
                if y > 0 and [x, y - 1] in my_dict[1]:
                    my_dict[1].remove([x, y - 1])
                    add_list.append([x, y - 1])

            my_dict[2].remove([x,y])
            my_dict[2] += add_list
            if prev_len == len(my_dict[1]):
                return -1
        return count
