class Solution {
    public int orangesRotting(int[][] grid) {
        //Build a list of all the rotten apples
		// This will be used for the BFS
        Queue<Identifier> queue = new LinkedList<>();
        for(int i =0; i < grid.length; i++){
            for(int j =0; j < grid[0].length; j++){
                if(2==grid[i][j]){
                    queue.add(new Identifier(i,j,0));
                }
            }
        }
        //depth = day
        int day=0;
        while(!queue.isEmpty()){
            Identifier badOrange = queue.remove();
            if(day<=badOrange.day){
                day=badOrange.day;
            }
			// look at all the adjecent cells for good oranges. 
			//Add the newly spoilt oranges to the list and update the value so that we don't end up in a cycle
            //up
            if(badOrange.row -1>=0 && grid[badOrange.row-1][badOrange.column]==1){
                grid[badOrange.row-1][badOrange.column]=2;
                queue.add(new Identifier(badOrange.row-1,badOrange.column, day+1));
            }
            //down
            if(badOrange.row+1< grid.length && grid[badOrange.row+1][badOrange.column]==1){
                grid[badOrange.row+1][badOrange.column]=2;
                queue.add(new Identifier(badOrange.row+1,badOrange.column, day+1));
            }
            //left
            if(badOrange.column-1>=0 && grid[badOrange.row][badOrange.column-1]==1){
                grid[badOrange.row][badOrange.column-1]=2;
                queue.add(new Identifier(badOrange.row,badOrange.column-1, day+1));
            }
            //right
            if(badOrange.column+1<grid[0].length && grid[badOrange.row][badOrange.column+1]==1){
                grid[badOrange.row][badOrange.column+1]=2;
                queue.add(new Identifier(badOrange.row,badOrange.column+1, day+1));
            }
        }
        //final check to see if any straggling oranges remain
        for(int i =0; i < grid.length; i++){
            for(int j =0; j < grid[0].length; j++){
                int val = grid[i][j];
                if(val!=2 && val !=0){
                    return -1;
                }
            }
        }
        return day;
    }
}
