    public int orangesRotting(int[][] grid) {
        int minutes=-1;

        // brute-force propagation of rotten-ness.
        for( boolean changed=true; changed; minutes++ ) {
            changed=false;

            // mark all fresh oranges with rotten neighbors
            for( int i=0; i<grid.length; i++ )
                for( int j=0; j<grid[i].length; j++ )
                    if( grid[i][j]==1 && rottenNeighbor( grid, i, j ) ) {
                        grid[i][j]=-2;
                        changed=true;
                    }

            // flip marked oranges to now be rotten
            if( changed ) for( int i=0; i<grid.length; i++ )
                for( int j=0; j<grid[i].length; j++ )
                    if( grid[i][j]==-2 ) grid[i][j]=2;
        }

        // Check if there's any fresh oranges left:
        for( int i=0; i<grid.length; i++ )
            for( int j=0; j<grid[i].length; j++ )
                if( grid[i][j]==1 ) return -1;

        return minutes;
    }

    private static boolean rottenNeighbor( int[][] grid, int x, int y ) {
        if( x>0 && grid[x-1][y]==2 ) return true;
        if( y>0 && grid[x][y-1]==2 ) return true;
        if( (x+1)<grid.length && grid[x+1][y]==2 ) return true;
        if( (y+1)<grid[x].length && grid[x][y+1]==2 ) return true;
        return false;
    }
