                }
                if (col < x - 1 && grid[row][col + 1] == 1) {
                    rotten.offer(new int[]{row, col + 1});
                    grid[row][col + 1] = 2;
                    freshOranges--;
                }
            }

            time++; // increment time counter for each level of BFS tree
        }

        // if there are still fresh oranges, return -1 to indicate it is impossible to rot all of them
        // otherwise, return elapsed time
        return freshOranges == 0 ? time - 1 : -1;
    }
}
```

