class Solution {
public:
    int orangesRotting(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        queue<node> q;
        int cntoranges = 0;
        for(int i  =0;i<n;i++){
            for(int j = 0;j<m;j++){
                if(grid[i][j] == 2){
                    q.push(node(i,j,0));
                }
                if(grid[i][j] != 0) cntoranges++;

            }

        }
        int ans = 0;
        int cnt =0;
        while(!q.empty()){
            int x = q.front().x;
            int y = q.front().y;
            int time = q.front().time;
