class Solution {
public:
    int orangesRotting(vector<vector<int>>& grid) {
        int ans=-1;
        int fresh=0;

        queue<pair<int,int>> q;
        for(int i=0;i<grid.size();i++)
        {
            for(int j=0;j<grid[i].size();j++)
            {
                if(grid[i][j]==1)
                fresh++;
                if(grid[i][j]==2)
                    q.push({i,j});
            }
        }
        // int d[5]={0, -1, 0, 1, 0};
        vector<int> d={-1,0,1,0,-1};
        while(!q.empty())
        {
            int sz=q.size();
            for(int i=0;i<sz;i++)
            {
                pair<int,int> p=q.front();
                q.pop();
                for(int j=0;j<4;j++)
                {
                    int r=p.first+d[j];
                    int c=p.second+d[j+1];
                    if(r>=0 && c>=0 && r<grid.size() && c<grid[0].size() && grid[r][c]==1)
                    {
                        fresh--;
                        grid[r][c]=2;
                        q.push({r,c});
                    }
                }
            }
            ans++;
        }
        if(fresh>0)
            return -1;
        if(ans==-1)
            return 0;

        return ans;
    }
};
