    public int orangesRotting(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;
        int time = 0;
        int[][] visited = new int[n][m];
        Queue<int[]> q = new LinkedList<int[]>();
        for(int i=0; i<n; i++) {
            for(int j=0; j<m; j++) {
                if(grid[i][j] == 2 && visited[i][j] == 0) {
                    q.add(new int[]{i,j});
                }

            }
        }
        time = bfs(grid, visited, q);
        for(int i=0; i<n; i++) {
                    for(int j=0; j<m; j++) {
                        if(grid[i][j] == 1 ) {
                         return -1;
                        }

                    }
                }

        return Math.max(time-1, 0);
    }

    public int bfs(int[][] grid, int[][] visited, Queue<int[]> q) {
        int n = grid.length;
        int m = grid[0].length;
        int count = 0;
        int[][] dir = {{-1,0}, {0,-1}, {1,0}, {0,1}};

        while(!q.isEmpty()) {
            int size = q.size();
            count++;
            while(size-- >0) {
            int[] temp = q.poll();
            visited[temp[0]][temp[1]] = 1;
            for(int[] k: dir) {
                int x = temp[0]+k[0];
                int y = temp[1]+k[1];
                if(x>=0 && x<n && y>=0 && y<m && visited[x][y] == 0 && grid[x][y] == 1) {
                    grid[x][y] = 2;
                    q.add(new int[]{x,y});
                }
            }
            }

        }
        return count;
    }
}
