public class Solution {
    public int OrangesRotting(int[][] grid) {    
        var result = 0;

        // 2D array to keep track of paths visited
        var visited = new bool[grid.Length][];
        for (int row = 0; row < grid.Length; ++row) {
            visited[row] = new bool[grid[row].Length];
        } 

        // The Answer is the max of all shortest distances to a rotten cell
        for (int row = 0; row < grid.Length; ++row) {
            for (int col = 0; col < grid[row].Length; ++col) {
                // Cell is fresh, get distance to rotten
                if (grid[row][col] == 1) {
                    result = Math.Max(result, ShortestPath(grid, row, col, 0, visited));
                }                
            }          
        }        
        return result == int.MaxValue ? -1 : result;
    }

    // Compute shortest distance to a rotten cell
    private int ShortestPath(int[][] grid, int row, int col, int depth, bool[][] visited) {
        // Make sure parameters are in bounds
        if (row < 0 || row >= grid.Length || col < 0 || col >= grid[row].Length) {
            return int.MaxValue;
        }    

        // Cell is rotten
        if (grid[row][col] == 2) {
            return depth;
        }

        // Path been visited
        if (visited[row][col]) {
            return int.MaxValue;
        }       

        var distance = int.MaxValue;

        // Set path as visited
        visited[row][col] = true;

        // Cell is fresh, keep searching
        if (grid[row][col] == 1) {
            // Search right
            distance = Math.Min(distance, ShortestPath(grid, row, col + 1, depth + 1, visited));

            // Search left
            distance = Math.Min(distance, ShortestPath(grid, row, col - 1, depth + 1, visited)); 

            // Search top
            distance = Math.Min(distance, ShortestPath(grid, row + 1, col, depth + 1, visited));

            // Search bottom
            distance = Math.Min(distance, ShortestPath(grid, row - 1, col, depth + 1, visited)); 
        }

        // Set path as un visited
        visited[row][col] = false;

        return distance;
    }
}
