class Solution {
public:
    bool isSafe(int i, int j, int n, int m){
        if(i<0 || i>=n || j<0 || j>=m) return 0;
        return 1;
    }
    bool allRotten(vector<vector<int>> &a){
        int i,j,n=a.size(),m=a[0].size();
        for(i=0;i<n;i++){
            for(j=0;j<m;j++){
                if(a[i][j]==1) return 0;
            }
        }
        return 1;
    }
    int orangesRotting(vector<vector<int>>& a) {
        int i,j,n=a.size(),m=a[0].size(),timeFrame,x,y;
        queue<vector<int>> q;
        for(i=0;i<n;i++){
            for(j=0;j<m;j++){
                if(a[i][j]==2){
                    q.push({0,i,j});
                }
            }
        }
        while(q.size()){
            vector<int> v=q.front(); q.pop();
            timeFrame=v[0], x=v[1], y=v[2];
            if(isSafe(x-1,y,n,m) && a[x-1][y]==1){
                a[x-1][y]=2;
                q.push({timeFrame+1, x-1, y});
            }
            if(isSafe(x+1,y,n,m) && a[x+1][y]==1){
                a[x+1][y]=2;
                q.push({timeFrame+1, x+1, y});
            }
            if(isSafe(x,y-1,n,m) && a[x][y-1]==1){
                a[x][y-1]=2;
                q.push({timeFrame+1, x, y-1});
            }
            if(isSafe(x,y+1,n,m) && a[x][y+1]==1){
                a[x][y+1]=2;
                q.push({timeFrame+1, x, y+1});
            }
        }
        if(allRotten(a)) return timeFrame;
        return -1;
    }
};
