class Solution {
    public int orangesRotting(int[][] grid) {

        Queue<Pair> q = new LinkedList<Pair>();

        int r = grid.length;
        int c = grid[0].length;
		int fresh = 0, rotten = 0;
		for(int i=0;i<r;i++) {

			for(int j=0;j<c;j++) {

				if(grid[i][j]==2) {

					q.add(new Pair(i,j));
					rotten++;
				}
				else if(grid[i][j]==1) {

					fresh++;
				}
			}
		}

		if(rotten==0 && fresh>0) {

			return -1;
		}


		int time = 0;

		while(!q.isEmpty() && fresh>0) {

			time++;
			int size = q.size();

			while(size-- >0 && fresh>0) {

				Pair g = q.peek();
				q.poll();

				int x = g.first;
				int y = g.second;

				if(isSafe(x-1,y,r,c) && grid[x-1][y]==1) {

					fresh--;
					q.add(new Pair(x-1,y));
					grid[x-1][y] = 0;
				}
				if(isSafe(x,y-1,r,c) && grid[x][y-1]==1) {

					fresh--;
					q.add(new Pair(x,y-1));
					grid[x][y-1] = 0;
				}
				if(isSafe(x+1,y,r,c) && grid[x+1][y]==1) {

					fresh--;
					q.add(new Pair(x+1,y));
					grid[x+1][y] = 0;
				}
				if(isSafe(x,y+1,r,c) && grid[x][y+1]==1) {

					fresh--;
					q.add(new Pair(x,y+1));
					grid[x][y+1] = 0;
				}
			}

		}
		if(fresh==0) return time;
		return -1;
    }
    boolean isSafe(int x, int y, int r, int c) {

		if(x>=0 && y>=0 && x<r && y<c) return true;
		return false;
	}
}

class Pair{

	int first;
	int second;

	public Pair(int first, int second) {

		this.first = first;
		this.second = second;
	}
}
