	class Solution {
	public:

    // to change the fresh oranges which will rot due to their neighbours
    void cor(vector<vector<int>> &grid) { 
        int r=grid.size(), c=grid[0].size();
        for (auto i=0;i<r;i++) for (auto j=0;j<c;j++) if(grid[i][j]==3) grid[i][j]=2;
    }

	// here we see whether there is a fresh orange in the neighbourhood of a rotten orange. If yes, we'll mark that '3' which will later be marked '2'(rotten) using cor()
    void repair(vector<vector<int>> &grid, int i, int j) { 
        if(i<0 || j<0 || i>=grid.size() || j>=grid[0].size()) return;

        if(grid[i][j]==1) grid[i][j]=3;
        return;
    }

	// we check whether there are any fresh oranges left in the grid
    bool check(vector<vector<int>> grid) { 
        int r=grid.size(), c=grid[0].size();
        for (auto i=0;i<r;i++) for (auto j=0;j<c;j++) if(grid[i][j]==1) return true;
        return false;
    }

    int orangesRotting(vector<vector<int>>& grid) {
        int r=grid.size(), c=grid[0].size();
        int ans=0; //to count the number of minutes

        while(check(grid)) {
		// observing the boundary conditions, if the count of minutes turns out to be more than let's say 100, than there is some fresh orange who is living in a full healthy neighbourhood. In short, mission failed for rotten oranges
            if(ans>100) return -1; 
            ans++;
            for (auto i=0;i<r;i++) {
                for (auto j=0;j<c;j++) 
                    if(grid[i][j]==2) {
					// Taking a stroll in the neighbourhood of this rotten orange
                        repair(grid,i-1,j);
                        repair(grid,i,j-1);
                        repair(grid,i,j+1);
                        repair(grid,i+1,j);
                    }
            }

            cor(grid);
        }

        return ans;
    }
};

