class Solution {

    // bfs .. lets make queue.
    Queue<Node> queue = new LinkedList<Node>();

    public int orangesRotting(int[][] grid) {
    int max = 0;

        for(int i=0;i<grid.length;i++) {

            for(int j=0;j<grid[i].length;j++) {

                if (grid[i][j]==2) {
                    Node node = new Node();
                    node.point.x = i;
                    node.point.y = j;
                    queue.add(node);
                } 
            }
        }

        while(!queue.isEmpty()) {

            Node node = queue.poll();
            int i = node.point.x;
            int j = node.point.y;
            int minutes = node.minutes;
            if (max<minutes)
                max = minutes;

            makeRotten(i+1, j,minutes, grid );
            makeRotten(i-1, j,minutes, grid);
            makeRotten(i, j+1,minutes, grid);
            makeRotten(i, j-1,minutes, grid);

        }

        for(int i=0;i<grid.length;i++) {
            for(int j=0;j<grid[i].length;j++) {

                if (grid[i][j]==1) {
                   return -1;
                } 
            }
        }

        return max;
    }

    public boolean makeRotten(int i, int j,int minutes, int[][] grid) {

        // if no fresh orange found then return 
        if (i<0 || i>=grid.length || j<0 || j>=grid[i].length ||
           grid[i][j]!=1) {
            return false;
        }

       // if fresh orange found then make it to rotten and add to queue with counter incremented 
        grid[i][j]=2;
        Node node = new Node();
        node.point.x=i;
        node.point.y=j;
        node.minutes = minutes+1;
        queue.add(node); 
        return true;

    }

