            for(int k = 0; k < size; k++) {
                int[] coordinate = queue.poll();

                for (int[] dir: dirs) {
                    int x = coordinate[0] + dir[0];
                    int y = coordinate[1] + dir[1];

                    if (x < 0 || y < 0 || x >= cols || y >= rows) continue;

                    if (grid[y][x] == 1) {
                        grid[y][x] = 2;
                        queue.add(new int[]{ x, y });
                        numFresh--;
                    }
                }
            }
            minutes++;
        }

        return numFresh > 0 ? -1 : minutes-1;
    }
}
