    private int row, col;
    public int orangesRotting(int[][] grid) {
        row = grid.length;
        col = grid[0].length;
        for (int i = 0; i < row; i++) {
            for (int j = 0; j < col; j++) {
                if (grid[i][j] == 2) {
                    dfs(grid, i, j , 0, true);
                }
            }
        }

        int max = 0;
        for (int[] ints : grid) {
            for (int anInt : ints) {
                if (anInt == 1) {
                    return -1;
                }
                max = Math.min(max, anInt);
            }
        }
        return -max;
    }


    private void dfs(int[][] grid, int i, int j, int dist, boolean begin) {
        if (i >= row || i < 0 || j >= col || j < 0 || grid[i][j] == 0 || grid[i][j] < 0 && -grid[i][j] < dist) return;

        if (grid[i][j] == 2 && !begin) {
            return;
        }
        grid[i][j] = -dist;
        dfs(grid, i + 1, j, dist + 1, false);
        dfs(grid, i - 1, j, dist + 1, false);
        dfs(grid, i, j - 1, dist + 1, false);
        dfs(grid, i, j + 1, dist + 1, false);
    }


