class Solution {
public:
    int orangesRotting(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int count_fresh_orange = 0;  // Count of fresh oranges
        int ans = 0;  // count time

        // Queue to store the positions of rotten oranges
        queue<pair<int, int>> q;

        // Count fresh oranges and enqueue rotten oranges
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                if (grid[i][j] == 1) {
                    count_fresh_orange++;  // Count the number of fresh oranges
                }
                else if (grid[i][j] == 2) {
                    q.push({i, j});   // Enqueue rotten orange
                }
            }
        }

        int dir[4][2] = {{-1, 0}, {1, 0}, {0, -1}, {0, 1}};  // Possible directions for rotting

        // Perform BFS
        while (!q.empty()) {
            int size = q.size();
            for (int k = 0; k < size; k++) {   // If there are 2 rotten oranges, they can rot other fresh oranges simultaneously
                int x = q.front().first;
                int y = q.front().second;
                q.pop();

                for (int i = 0; i < 4; i++) {
                    int xi = x + dir[i][0];
                    int yi = y + dir[i][1];
                    if (xi >= 0 && xi < n && yi >= 0 && yi < m && grid[xi][yi] == 1) {
                        count_fresh_orange--;
                        grid[xi][yi] = 2;  // Mark the orange as rotten
                        q.push({xi, yi});  // Enqueue the newly rotten orange
                    }
                }
            }
            ans++;  // Increment time 
        }

        if (count_fresh_orange > 0) {
            return -1;  // Not all oranges can be rotten
        }

        // Subtract 1 from ans since we started counting from 0
        return ans > 0 ? ans - 1 : 0;
