# BFS
```
class Solution {
public:
    int orangesRotting(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();    
        int time = 0;

        // row, col, time
        queue<array<int, 3>> Q;

        for (int r = 0; r < m; r++) {
            for (int c = 0; c < n; c++) {
                if (grid[r][c] == 2)
                    Q.push({r, c, 0});
            }
        }

        int DIR[][2] = { {0, -1}, {1, 0}, {0, 1}, {-1, 0} };

        while (!Q.empty()) {
            int r = Q.front()[0];
            int c = Q.front()[1];
            int t = Q.front()[2];
            Q.pop();

            time = max(time, t);

            for (int i = 0; i < 4; i++) {
                int nr = r + DIR[i][0], nc = c + DIR[i][1];
                if (nr < 0 || nc < 0 || nr == m || nc == n || grid[nr][nc] != 1)
                    continue;
                grid[nr][nc] = 2; // rotten
                Q.push({nr, nc, t + 1});
            }
        }

        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (grid[i][j] == 1)
                    return -1;
            }
        }

        return time; 
    }
};
```

