        for(int i=0; i<n; i++){
            for(int j=0; j<m; j++){
                if(grid[i][j] == 2){
                    que.push({i,j});
                }
            }
        }

        int r[4] = {-1, 1, 0, 0};
        int c[4] = {0, 0, -1, 1}; 

