class Solution {
public:
    int orangesRotting(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int vis[n][m];
        // queue will store coordinates and corresponding time
        queue<pair<pair<int, int>, int>> q;

        for(int i = 0; i < n; i++){
            for(int j = 0; j < m; j++){
                if(grid[i][j] == 2){
                    // initial vertices will be the rotten ones
                    q.push({{i,j}, 0});
                    vis[i][j] = 2;
                }
            }
        }

        // initializing directions for bfs traversal
        int dx[] = {-1,0,1,0};
        int dy[] = {0,-1,0,1};
        int time = 0;

        while(!q.empty())
        {
            int r = q.front().first.first, c = q.front().first.second, t = q.front().second;
            time = max(time, t);
            q.pop();
            for(int i = 0; i < 4; i++){
                int nrow = r + dx[i], ncol = c + dy[i];
                // if all conditions satisfy then push the neighbouring row, col into the queue
                // while also updating the time and vis
                if (nrow >= 0 && ncol >= 0 && nrow < n && ncol < m && vis[nrow][ncol] != 2 && grid[nrow][ncol] == 1)
                {
                    q.push({{nrow, ncol}, t + 1});
                    vis[nrow][ncol] = 2;
                }
            }
        }

        // in the end check for the -1 case
        // this happens when for any (i,j) vis != 2 and the grid val = 1
        for(int i = 0; i < n; i++){
            for(int j = 0; j < m; j++){
                if(vis[i][j] != 2 && grid[i][j] == 1){
                    return -1;
                }
            }
        }
        return time;
    }
};
