class Solution {
public:
    int orangesRotting(vector<vector<int>>& grid) {

        int m = grid.size();// store grid row size
        int n = grid[0].size();// store grid coloum size

        vector<vector<int>> toVisit;// to store rotten orrange cells to be visited.

        int i,j,time=0;

        for(i=0;i<m;i++)
        {
            for(j=0;j<n;j++)
            {
                if(grid[i][j]==2)
                {
                    toVisit.push_back({i,j}); // if cell is rooten than add it to toVisit.
                }
            }
        }

        toVisit.push_back({-1,-1});// adding a time breaker for each iteration.
        int r,c;

        while(!toVisit.empty())
        {
            while(toVisit.front()[0]!=-1)// run till it hits breaker
            {
                r=toVisit.front()[0];// get current row
                c=toVisit.front()[1];// get current coloum

                toVisit.erase(toVisit.begin());// erase current cell

                if(r>0)// check for up cell
                {
                    if(grid[r-1][c]==1)// if orange is freash then rotten it and add to the toVist list.
                    {
                        grid[r-1][c]=2;
                        toVisit.push_back({r-1,c});
                    }
                }
                if(r<m-1)// check for down cell
                {
                    if(grid[r+1][c]==1)// if orange is freash then rotten it and add to the toVist list.
                    {
                        grid[r+1][c]=2;
                        toVisit.push_back({r+1,c});
                    }
                }
                if(c>0)// check for left cell
                {
                    if(grid[r][c-1]==1)// if orange is freash then rotten it and add to the toVist list.
                    {
                        grid[r][c-1]=2;
                        toVisit.push_back({r,c-1});                 
                    }
                }
                if(c<n-1)// check for right cell
                {
                    if(grid[r][c+1]==1)// if orange is freash then rotten it and add to the toVist list.
                    {
                        grid[r][c+1]=2;
                        toVisit.push_back({r,c+1});
                    }
                }
            }

            toVisit.erase(toVisit.begin());// erase the time breaker
            if(!toVisit.empty())// add time breaker if toVisit is not empty
                toVisit.push_back({-1,-1});

            time++;//increment time
        }

        int check=0;// to check for fresh orange
        for(i=0;i<m;i++)
        {
            for(j=0;j<n;j++)
            {
                if(grid[i][j]==1)
                {
                    check=1;
                }
            }
        }

        if(check==1)
            return -1;
        else
            return time-1;
    }
};
