class Solution {
public:
    int orangesRotting(vector<vector<int>>& grid) {
        queue<pair<int,int>>q;
        int rows = grid.size();
        int cols = grid[0].size();

        int ones=0,twos=0,ans=0;

        //Count number of 2's and 1's and push all 2's in the queue
        for(int i=0;i<rows;i++){
            for(int j=0;j<cols;j++){
                if(grid[i][j] == 2){
                    twos++;
                    q.push({i,j});
                }
                else if(grid[i][j]==1){
                    ones++;
                }
            }
        }

        //if there are no fresh oranges present(i.e number of ones is equal to zero), time required will also be zero
        if(ones == 0) return 0;

        //else if there are no rotten oranges, fresh oranges will not rot and we will have to return (-1)
        else if(twos == 0) return -1;

        // count1 stores the number of unvisited nodes or oranges which are 
        // going to affect its neighbours at that moment
        // count2 is going to store number of children nodes of the oranges in the current level
        int count1=twos, count2=0;
        // assuming the first level is already rotten, we are counting it and so at the end we will return (ans-1) instead of ans. 
		//(If you want to return ans, you can initialize ans as -1 above😉 )

        //BFS traversal
        while(!q.empty()){
            int i = q.front().first;
            int j = q.front().second;
            q.pop();
            // Everytime we pop, unvisited oranges in current level decreases by one, hence count1-- (That is decrementing number of 2's)
            count1--;

            //there are four conditions(Top, Bottom, Left, Right) to check for the current orange
			//and it should not cross the bounds of the grid so using checkValid Function at the end

            //Check for Top Direction
            if(checkValid(i+1,j,rows,cols) && grid[i+1][j]==1){
                // 1(fresh orange) becomes 2 (Rotten Orange) 
                // and number of children i.e. count2 increased by one
                // and push it to queue to further check its children nodes
                q.push({i+1,j});
                grid[i+1][j] = 2;
                count2++;
                ones--;
            }
            //Check for Bottom Direction
            if(checkValid(i-1,j,rows,cols) && grid[i-1][j]==1){
                q.push({i-1,j});
                grid[i-1][j] = 2;
                count2++;
                ones--;
            }
            //Check for Left Direction
            if(checkValid(i,j-1,rows,cols) && grid[i][j-1]==1){
                q.push({i,j-1});
                grid[i][j-1] = 2;
                count2++;
                ones--;
            }
            //Check for Right Direction
                if(checkValid(i,j+1,rows,cols) && grid[i][j+1]==1){
                q.push({i,j+1});
                grid[i][j+1] = 2;
                count2++;
                ones--;
            }

            if(count1 == 0){ //Traversed for all 2's
                // count1 is zero means we have completed traversing the current level of the graph
                // and hence one unit of time is passed. So we increrment ans
                // going to the next level, for that count2 becomes count1 and count2 becomes zero, 
                // i.e. children nodes become parent nodes to their children and child count again re-initialised to zero.
                ans++;
                count1 = count2;
                count2 = 0;
            }
        }

        //If there are still some numbers of fresh oranges left(isolation condition)
        //so in that case return -1;
        if(ones > 0) return -1;

        //else return our answer
        return ans-1;
    }

    //Function to check out of bound condition
    bool checkValid(int i,int j,int rows, int cols){
        if(i<0 || i>=rows || j<0 || j>=cols){
            return false;
        }
        return true;
    }
};
