class Solution {
public:
    int orangesRotting(vector<vector<int>>& grid) {
        if(grid.empty()) return 0;
        int m=grid.size(), n=grid[0].size(), days=0, tot=0, cnt=0;
        queue<pair<int, int>>q;

        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(grid[i][j]!=0) tot++;//count total oranges
                if(grid[i][j]==2) q.push({i, j});//keep track of rotten orange
            }
        }
        //store the all possible directions
        int dx[4]={0, 0, 1, -1};
        int dy[4]={1, -1, 0, 0};

        while(!q.empty()){
            int k=q.size();
            cnt+=k;//updating total rotten orange
            while(k--){//do the following steps untill all rotten orange in queue are processed
                int x=q.front().first, y=q.front().second;
                q.pop();
                for(int i=0;i<4;i++){
                    int nx=x + dx[i], ny= y+ dy[i];//in each itration nx & ny go to all possible direction
                    //checking boundery cases & avoid the rotten orange cells & empty cells
                    if(nx< 0 || ny< 0 || nx>=m || ny>=n || grid[nx][ny]!=1) continue;
                    grid[nx][ny]=2;//other wise marks the cell as rotten
                    q.push({nx,ny});//update the queue with new rotten cells
                }
            }
            if(!q.empty()) days++;//if queue is not empty, then is makes sure that there is some rotten oranges,and they take another days or times  
        }
        return tot==cnt? days: -1;//if all oranges are rotten, then return the days count ,other wise -1;
    }
};
