class Solution:
    def orangesRotting(self, grid: List[List[int]]) -> int:
        m, n, remainFreshOrange = len(grid), len(grid[0]), 0
        bfs = deque([])
        for r in range(m):
            for c in range(n):
                if grid[r][c] == 2:
                    bfs.append((r, c))
                    grid[r][c] = 0
                elif grid[r][c] == 1:
                    remainFreshOrange += 1

        DIR = [0, 1, 0, -1, 0]
        minute = 0
        while bfs and remainFreshOrange > 0:
            for _ in range(len(bfs)):
                r, c = bfs.popleft()
                for i in range(4):
                    nr, nc = r + DIR[i], c + DIR[i+1]
                    if nr < 0 or nr == m or nc < 0 or nc == n or grid[nr][nc] != 1: continue
                    grid[nr][nc] = 0
                    remainFreshOrange -= 1
                    bfs.append((nr, nc))
            minute += 1
        if remainFreshOrange > 0: return -1
        return minute
