class Solution {
    class Pair{
        TreeNode root;
        int val;
        Pair(TreeNode root, int val) {
            this.root = root;
            this.val = val;
        }
    }
    public int goodNodes(TreeNode root) {
        if(root == null) return 0;
        int count=0;
        Stack<Pair> s = new Stack<>();
        s.push(new Pair(root,root.val));
        while(!s.isEmpty()) {
            Pair p = s.peek();
            TreeNode t  =p.root;
            if(t.left == null && t.right == null) {
                s.pop();
                if(t.val >= p.val) count++;
            } else if(t.left != null) {
                s.push(new Pair(t.left, Math.max(t.left.val, p.val)));
                t.left = null;
            } else if(t.right != null) {
                s.push(new Pair(t.right, Math.max(t.right.val, p.val)));
                t.right = null;
            }
        }

     return count;
    }
}
