int rec(struct TreeNode* root, int max) {
    if (!root)
        return 0;
    max = root->val > max ? root->val : max;
    if (root->val >= max)
        return 1+rec(root->right, max) + rec(root->left, max);
    else
        return rec(root->right, max) + rec(root->left, max);
}

int goodNodes(struct TreeNode* root){
    if (!root) return 0;
    return rec(root, root->val);
}
