/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    int ans=0;

    void update(TreeNode* root, TreeNode* parent, int max){
        if(!root)
        return;

        if(root->val>=parent->val && root->val>=max){
            ans++;
            max=root->val;
        }

        update(root->left, root, max);
        update(root->right, root, max);

        return;
    }

    int goodNodes(TreeNode* root) {
        if(!root->left && !root->right)
        return 1;

        update(root, root, root->val);
        return ans;
    }
};
