func goodNodes(root *TreeNode) int {
    return goodNodesTraversal(root, math.MinInt)
}

func goodNodesTraversal(root *TreeNode, maxVal int) int {
    if root == nil {
        return 0
    }

    goodNode := 0
    if root.Val >= maxVal {
        maxVal = root.Val
        goodNode += 1
    }

    if root.Left != nil {
        goodNode += goodNodesTraversal(root.Left, maxVal)
    }

    if root.Right != nil {
        goodNode += goodNodesTraversal(root.Right, maxVal)
    }

    return goodNode
}

