void traverse(TreeNode* root,int k,int& count)//k is not reference to make it hold the -
    {                                             // max value of ancestors just before current node
        if(root==NULL)
            return ;
        if(k<root->val)
            k=root->val;               //value of k holds the max value among ancestors
        traverse(root->left,k,count);
            if(root->val>=k)
                count++;               //value of count keep track of good nodes
        traverse(root->right,k,count);
    }
    int goodNodes(TreeNode* root) {
        int count=0;
        traverse(root,INT_MIN,count);
        return count;
    }
