class Solution {
public:
    bool areSentencesSimilar(string s1, string s2) {
        if(s1.size() < s2.size()) {
            string temp = s1;
            s1 = s2;
            s2 = temp;
        }
        if(s1.compare(s2) == 0) return 1;

        deque<string> words1, words2;

        int i = 0;

        while(i < s1.size()) {
            int j = i;
            while(j < s1.size() && s1[j] != ' ' ) {
                j++;
            }
            string temp = s1.substr(i, j-i);
            words1.push_back(temp);
            i = j+1;
        }

        i = 0;

        while(i < s2.size()) {
            int j = i;
            while(j < s2.size() && s2[j] != ' ' ) {
                j++;
            }
            string temp = s2.substr(i, j-i);
            words2.push_back(temp);
            i = j+1;
        }

        bool used = false;

        while(words1.size() && words2.size()) {
            if(words1.front().compare(words2.front()) == 0) {
                words1.pop_front();
                words2.pop_front();
                continue;
            }

            if(used) return false;

            while(words1.size() && words2.size() && words1.front().compare(words2.front()) && !used) {
                words2.push_front(words1.front());
                words1.pop_front();
                words2.pop_front();
            }

            used = 1;

        }

        if(words2.size() == 0 && !used) return true;

        if(words1.size() == 0 && words2.size() == 0) return true;

        return false;

    }
};

