        // check the common suffix (words) and return the index of the last space of this common suffix
        int i=0;
        int last_space = -1;
        while (i<s1.size()) {
            if (s1[i] != s2[i])
                return last_space;  // discard the partial matching word
            if (s1[i] == ' ')
                last_space = i;
            ++i;
        }

        // s1 completely matches with the s1
        if (s2[i] == ' ')   // if the last word of s1 was also a word in s2 or was it partial word 
            return i;

        return last_space;
    }

    inline bool if_right_match(string s1, string s2, int left_i) {
        int i, j;
        for (i=s1.size()-1, j=s2.size()-1; i>left_i; --i, --j) {
            if(s1[i] != s2[j]){
                //cout << " " << i;
                return false;
            }
        }

        return s2[j] == ' ';
    }

    bool areSentencesSimilar(string sentence1, string sentence2) {

        if (sentence1.size() > sentence2.size())
            return areSentencesSimilar(sentence2, sentence1);
        if (sentence1.size() == sentence2.size())
            return sentence1 == sentence2;

        // 1 is smaller than 2
        int left_i = get_left_index_match(sentence1, sentence2);
        //cout << left_i;
        if (left_i == sentence1.size())
            return true;

        return if_right_match(sentence1, sentence2, left_i);

    }
};
