            if(v1.back()==v2.back()) v1.pop_back(),v2.pop_back();
            else break;
        }
			//after removing starting and ending common word if we find one of the deque is empty than result is true otherwise result is false
        if(v1.empty()||v2.empty()) return true;  
        else return false;
    }
