import "reflect"

func areSentencesSimilar(sentence1 string, sentence2 string) bool {
    s, l := strings.Fields(sentence1), strings.Fields(sentence2)
    if len(s) > len(l) {
        s, l = l, s
    }
    for i := range s {
        if s[i] == l[i] {
            continue
        }
        return reflect.DeepEqual(s[i:], l[i+len(l)-len(s):])
    }
    return true
}
