const ok = (short, long) => {
    let re = []; // record short array length
    while (1) {
        if (short[0] == long[0]) {
            short.shift();
            long.shift();
        }
        if (short[short.length - 1] == long[long.length - 1]) {
            short.pop();
            long.pop();
