class Solution {
public:
    bool areSentencesSimilar(string sentence1, string sentence2) {
        if(sentence1 == sentence2) return true;
        istringstream s1(sentence1),s2(sentence2);
        vector<string> svtr1,svtr2;
        string tmp;
        while(s1 >> tmp) svtr1.push_back(tmp);
        while(s2 >> tmp) svtr2.push_back(tmp);
        int len1 = svtr1.size();
        int len2 = svtr2.size();
        if(len1 < len2){
            swap(len1,len2);
            swap(svtr1,svtr2);
        }
        int l1=0,l2=0,r1 = len1-1,r2=len2-1; 
        // Check from beginning
        while(l1 <= r1 && l2 <= r2 && svtr1[l1] == svtr2[l2]){
            if(r2 == l2)
                return true;
            ++l1;
            ++l2;
        }
        // Check from ending
        while(l1 <= r1 && l2 <= r2 && svtr1[r1] == svtr2[r2]){
            if(r2 == l2)
                return true;
            --r1;
            --r2;
        }
        return false;
    }
};
