class Solution {
public:
    bool areSentencesSimilar(string s1, string s2) {
        stringstream s(s1),ss(s2);

        vector<string>v1,v2;

        string word;

        while(s>>word) v1.push_back(word);
        while(ss>>word) v2.push_back(word);

        int i=0,j=0;

        while(i<v1.size() and j<v2.size()){
            if(v1[i]==v2[j]) i++,j++;
            else break;
        }

        int x=v1.size()-1,y=v2.size()-1;

        bool ans=true;

        while(i<=x and j<=y){
            if(v1[x]==v2[y]) x--,y--;
            else{
                ans=false;
                break;
            }
        }
        return ans;
    }
};
