class Solution {
public:
    bool areSentencesSimilar(string sentence1, string sentence2) {
        if(sentence1 == sentence2) 
            return true;

        vector<string> a;
        vector<string> b;
        int m = sentence1.size();
        int n = sentence2.size();

        for(int i=0; i<m; i++){
            string t;
            while(i < m && sentence1[i] != ' '){
                t += sentence1[i++];
            }

            if(t.size())
                a.push_back(t);
        }

        for(int i=0;i<n;i++){
            string t;
            while(i<n && sentence2[i] != ' '){
                t += sentence2[i++];
            }

            if(t.size())
                b.push_back(t);            
        }

        int min_ = min(a.size(), b.size());
        int cnt = min_;

        int i = 0;
        int j = 0;

        while(i < a.size() && j < b.size()){
            if(a[i] == b[j]){
                cnt--;
                i++; 
                j++;

                if(cnt == 0) 
                    return true;
            }
            else
                break;
        }

        i = a.size()-1;
        j = b.size()-1;

        while(i >= 0 && j >=0 ){
            if(a[i] == b[j]){
                cnt--;
                i--; 
                j--;

                if(cnt == 0) 
                    return true;
            }
            else
                break;             
        }
        return cnt == 0;
    }
};
