# Code
```
class Solution {
    func areSentencesSimilar(_ sentence1: String, _ sentence2: String) -> Bool {
        let count1 = sentence1.count
        let count2 = sentence2.count
        if count1 > count2 {
            return areSentencesSimilar(sentence2, sentence1)
        }
        if count1 == count2 {
            return sentence1 == sentence2
        }

        var idx = sentence1.startIndex
        var lastSpaceIdx: String.Index? = nil
        while idx < sentence1.endIndex && sentence1[idx] == sentence2[idx] {
            if sentence1[idx] == Character(" ") {
                lastSpaceIdx = idx
            }
            sentence1.formIndex(after: &idx)
        }

        if sentence2[idx] == Character(" ") && idx == sentence1.endIndex { return true } else {
            idx = lastSpaceIdx ?? sentence1.startIndex
        }

        var jdx = sentence1.endIndex
        sentence1.formIndex(before: &jdx)
        var longJdx = sentence2.endIndex
        sentence2.formIndex(before: &longJdx)
        while jdx != idx && sentence1[jdx] == sentence2[longJdx] {
            sentence1.formIndex(before: &jdx)
            sentence2.formIndex(before: &longJdx)
        }

        if jdx == idx, sentence1[jdx] == sentence2[longJdx] {
            if lastSpaceIdx != nil { return true }
            sentence2.formIndex(before: &longJdx)
            return sentence2[longJdx] == Character(" ")
        }

        return false
    }
}
