                    if(!words2[j].equals(words1[j])){
                        break;
                    }
                }
                if(j != i+1){
                    continue;
                }
                for(k = 0;k < words2.length-j;k++){
                    if(!words2[words2.length-1-k].equals(words1[words1.length-1-k])){
                        break;
                    }
                }
                if(k == words2.length-j){
                    return true;
                }
            }
            return false;
        }
