class Solution {
    fun areSentencesSimilar(sentence1: String, sentence2: String): Boolean {
        val splitOne = sentence1.split(" ").toMutableList()
        val splitTwo = sentence2.split(" ").toMutableList()

        // find where they are not equal
        while (true) {
            if (splitOne.isEmpty() || splitTwo.isEmpty()) break
            if (splitOne.first() != splitTwo.first()) break

            splitOne.removeAt(0)
            splitTwo.removeAt(0)
        }

        if (splitOne.size == splitTwo.size) return splitOne == splitTwo

        val shorterList = if (splitOne.size < splitTwo.size) splitOne else splitTwo
        val longerList = if (shorterList == splitOne) splitTwo else splitOne

        while (longerList.isNotEmpty() && longerList.size >= shorterList.size && longerList != shorterList) {
            longerList.removeAt(0)
        }

        return shorterList == longerList
    }
}
