class Solution {
    public boolean areSentencesSimilar(String sentence1, String sentence2) {
        if (sentence1.length()<sentence2.length()) return areSentencesSimilar(sentence2,sentence1);
        if (sentence1.equals(sentence2) ||
            (sentence1.startsWith(sentence2) && (sentence1.charAt(sentence2.length()) == ' ')) || 
            (sentence1.endsWith(sentence2)) && (sentence1.charAt(sentence1.length()-sentence2.length()-1) ==  ' ')) return true;

        while (sentence2.length()>0){
            int wordLen = sentence2.indexOf(' ');
            String word = (wordLen!=-1)? sentence2.substring(0,wordLen) : sentence2;

            if (!sentence1.startsWith(word) || sentence1.charAt(word.length())!=' ') return false;
            sentence2 = sentence2.substring(wordLen + 1);
            if (sentence1.endsWith(sentence2) && sentence1.charAt(sentence1.length()-sentence2.length()-1)==' ') return true;
            sentence1 = sentence1.substring(wordLen + 1);
        }
        return true;
    }
}
