class Solution {
private:
    vector<string> generateVector(string sentence){
    int n = sentence.length();
    vector<string> ans;
    int j = 0;
    for(int i = 0; i < n; i++){
        if(sentence[i] == ' ' || i == n-1){
            string temp = sentence.substr(j, i - j + (i == n - 1 ? 1 : 0));
            ans.push_back(temp);
            j = i + 1;
        }
    }
    return ans;
}
public:
    bool areSentencesSimilar(string sentence1, string sentence2) {
        vector<string> sent1 = generateVector(sentence1);
        vector<string> sent2 = generateVector(sentence2);

        int n1 = sent1.size(); int n2 = sent2.size();
        int i=0; int j=0; 
