        {
            if(sent1[i] == sent2[j])
            {
                i++; 
                j++;
            }
            else{
                break;
            }
        }

        int index1 = i, index2 = j;
        i = n1-1; j = n2-1;
        while(i >= index1 and j >= index2)
        {
            if(sent1[i]==sent2[j])
            {
                i--; 
                j--;
            }
            else{
                return false;
            }
        }
        return true;

    }
};
