class Solution {
public:
    bool solve(string &s1, string &s2)
    {
        int n1 = s1.size(), n2 = s2.size(), l=0, last = -1;
        while(l<n2 && s1[l] == s2[l])
        {
            if(s1[l] == ' ')last = l;
            l++;
        }
        if(l == n2 && s1[l] == ' ')return true;
        int rem = n2-1-last, r1 = n1-1, r2 = n2-1;
        // cout << rem << ' ';
        while(rem>0)
        {
            if(s1[r1] == s2[r2]){r1--;r2--;}
            else break;
            rem--;
        }
        // cout << last <<  " "  << r1 << " "  << r2 << " "  << rem << '\n';
        return (rem == 0 && (r2 == -1 || s2[r2] == ' ') && s1[r1] == ' ');
    }
    bool areSentencesSimilar(string s1, string s2) {
        if(s1.size() == s2.size())return s1 == s2;
        if(s1.size() > s2.size())return solve(s1, s2);
        return solve(s2, s1);
    }
};
