class Solution {
    public boolean areSentencesSimilar(String s1, String s2) {
        //Split the array.
        String[] str1 = s1.split(" ");
        String[] str2 = s2.split(" ");
        //check which string have the more words and less words so we can put words in lesser sentence array to make it equal
        return str1.length<=str2.length ? check(str1,str2) : check(str2,str1);
        //by this fn we ensure s1 have more words than s2 
    }

    public boolean check(String[] str1, String[] str2){
        int diff = 0;
        int i=0;

        //taking length of both strings
        int len1 = str1.length;
        int len2 = str2.length;

        //checking the first word of the array
        while(i<len1 && str1[i].equals(str2[i])){
            i++;
            diff++;
        }
        //checking the last word of the array
        while(i<len1 && str1[i].equals(str2[len2-len1+i])){
            i++;
            diff++;
        }
        //if the length of the lesser words array is equal to the difference then return
        return len1==diff;
    }
}
