object Solution {
    def areSentencesSimilar(sentence1: String, sentence2: String): Boolean = {
        def helper(s1: Vector[String], s2: Vector[String]): Boolean = (s1, s2) match {
            case (Nil, _) => true
            case (_, Nil) => true
            case (h1 +: r1, h2 +: r2) if (h1 == h2) => helper(r1, r2)
            case (r1 :+ t1, r2 :+ t2) if (t1 == t2) => helper(r1, r2)
            case _ => false
        }

        helper(sentence1.split(" ").toVector, sentence2.split(" ").toVector)
    }
}
