class Solution {
    public boolean areSentencesSimilar(String sentence1, String sentence2) {
        if(sentence1.length()==sentence2.length()){   // Basic comparison !
            return sentence1.equals(sentence2)?true:false;
        }
        String big,low;
        if(sentence1.length()>sentence2.length()){
            big=sentence1;
            low=sentence2;
        }else{
            big=sentence2;
            low=sentence1;
        }             // seperating the two strings on the basis of their length.
        return evaluate(big,low);    // function made by me.
    }
    boolean evaluate(String a , String b){
        String[] big=a.split(" ");   //coverting strings into string arrays for easy iteration.
        String[] low = b.split(" ");
        int l= low .length;
        int i=0;
        while(i<l){      //iterarting over shorter length so that out of bound is error is avoided.
           if(!big[i].equals(low[i])){
                break;
            }
            else{
                i++;
            }
        }
        if(i==l){  // condition when the shorter string is prefix of bigger string.
            return true;
        }
        int leni=big.length-1;  // checking for the suffix part(if equal) after getting prefix checked
        int j = l-1;      //                   0   1    2   3 
        while(j>=i){  // till i because ex--; "My name is Haley" and "My name" first common prefix would be checked which exists till i=0 only and in suffix part we check from last index of shorter one to i.
            if(!big[leni].equals(low[j])){
                return false;  // this means that neither prefix nor suffix part of big string is similar to shorter string.
            }
            else{
                j--;
                leni--;
            }
        }
        return true; 
    }
}
