def areSentencesSimilar(self, s1: str, s2: str) -> bool:
	if len(s1)<len(s2):
		s1, s2 = s2, s1
	if s1.startswith(s2+' ') or s1.endswith(' '+s2) or s1==s2:
		return True
	space_idx = [i for i in range(len(s2)) if s2[i]==' ']
	for i in space_idx:
		# space should be included in matching substring
		if s1.startswith(s2[:i+1]) and s1.endswith(s2[i:]):
			return True
	return False
